sys_ui_action.script:
function completeOffer() {
	/*
 	 * Make sure final disposition is populated first
 	 * If offer isn't complete, don't require the other mandatory field checks
 	 */

	if (g_form.getValue('u_final_disposition') == '') {
		//Remove any existing field message, set fields mandatory, and show a new field message
		try {g_form.hideFieldMsg('u_final_disposition');} catch(e) {}
		g_form.setMandatory('u_final_disposition', true);
		g_form.showFieldMsg('u_final_disposition','Please select an option','error');
		return false;  //Abort submission
	}

	if (g_form.getValue('u_final_disposition') == 'complete') {

		if (g_form.getValue('u_employee_id') == '') {
			//Remove any existing field message, set fields mandatory, and show a new field message
			try {g_form.hideFieldMsg('u_employee_id');} catch(e) {}
			g_form.setMandatory('u_employee_id', true);
			g_form.showFieldMsg('u_employee_id','Please populate employee ID','error');
			return false;  //Abort submission
		}

		if (g_form.getValue('u_docusign_req') == 'true' && g_form.getValue('u_docusign_completed') == 'false') {
			//Remove any existing field message, set fields mandatory, and show a new field message
			try {g_form.hideFieldMsg('u_docusign_completed');} catch(e) {}
			g_form.setMandatory('u_docusign_completed', true);
			g_form.showFieldMsg('u_docusign_completed','Please confirm DocuSign routing has completed','error');
			return false;  //Abort submission
		}

		if (g_form.getValue('u_offer_letter_req') == 'true' && g_form.getValue('u_offer_letter_signed') == 'false') {
			//Remove any existing field message, set fields mandatory, and show a new field message
			try {g_form.hideFieldMsg('u_offer_letter_signed');} catch(e) {}
			g_form.setMandatory('u_offer_letter_signed', true);
			g_form.showFieldMsg('u_offer_letter_signed','Please confirm Offer Letter has been signed','error');
			return false;  //Abort submission
		}
	}

	gsftSubmit(null, g_form.getFormElement(), 'complete_offer'); //MUST call the 'Action name' set in this UI Action

}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if (typeof window == 'undefined')
	serverActions();

function serverActions() {
	current.state = 4;
	current.update();
	gs.addInfoMessage("Offer has been closed.");
}
