answer = ifScript();

function ifScript() {
	
	/* check if user who opened offer is part of admin or sales/ops group */
	var memberGR = new GlideRecord('sys_user_grmember');
	var str = 'group=df387d074f807e0056b80195f110c7ef^ORgroup=c1fd23434f3fba0056b80195f110c7d7^user=' + current.opened_by;
	memberGR.addEncodedQuery(str);
	memberGR.query();
	
	if ((current.opened_by.title == "Regional Vice President of Sales") || ( memberGR.hasNext() )) {
		return 'yes';
	}
	return 'no';
}

